import { BottomTabBar } from "@react-navigation/bottom-tabs";
import { StyleSheet } from "react-native";

export const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  header: {
    padding: 20,
    backgroundColor: "#6b46c1",
  },
  title: {
    fontSize: 28,
    fontWeight: "bold",
    color: "#fff",
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 16,
    color: "#e9d8fd",
    marginBottom: 8,
  },
  content: {
    padding: 16,
  },
  muscleCard: {
    backgroundColor: "#fff",
    borderRadius: 12,
    padding: 16,
    marginBottom: 16,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    elevation: 2,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "space-between",
  },
  muscleName: {
    fontSize: 16,
    fontWeight: "600",
    color: "#2d3748",
    flex: 1,
  },
  progressContainer: {
    marginHorizontal: 16,
  },
  recoveryText: {
    fontSize: 14,
    color: "#718096",
    flex: 1,
    textAlign: "right",
  },
});
