import React, { useState, useEffect, useCallback } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  FlatList,
  TextInput,
  StyleSheet,
  ScrollView,
  ActivityIndicator,
  Alert,
  Platform,
} from "react-native";
import { Ionicons } from "@expo/vector-icons";
import {
  getExercises,
  getMuscleGroups,
  initDatabase,
} from "../database/database";
import { useFocusEffect } from "@react-navigation/native";

const AddExerciseScreen = ({ navigation, route }) => {
  const { muscleGroup, muscleGroupId, previousExercises, returnToPreset } =
    route?.params || {};
  // Fallback to empty array if previousExercises is undefined
  const safePreviousExercises = Array.isArray(previousExercises)
    ? previousExercises
    : [];
  // Initialize selectedExercises with previousExercises if provided
  const [selectedExercises, setSelectedExercises] = useState(
    safePreviousExercises
  );
  const [searchQuery, setSearchQuery] = useState("");
  const [activeGroup, setActiveGroup] = useState("All");
  const [muscleGroups, setMuscleGroups] = useState([]);
  const [exercises, setExercises] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [fatalError, setFatalError] = useState(null);

  useFocusEffect(
    useCallback(() => {
      if (route.params?.newExerciseToAdd) {
        // Add the new exercise to the previously selected list and return to ConfigureWorkout
        const exerciseToAdd = route.params.newExerciseToAdd;
        navigation.setParams({ newExerciseToAdd: undefined }); // Clear param
        navigation.navigate("ConfigureWorkout", { addExercise: exerciseToAdd });
      }
    }, [route.params?.newExerciseToAdd])
  );

  useEffect(() => {
    const safeLoadData = async () => {
      try {
        await loadData();
      } catch (err) {
        setFatalError('A fatal error occurred: ' + (err && err.message ? err.message : err));
        console.error('AddExerciseScreen fatal error:', err);
      }
    };
    safeLoadData();
  }, []);

  useEffect(() => {
    const { muscleGroup, muscleGroupId } = route.params || {};
    if (muscleGroup && muscleGroupId) {
      setActiveGroup(muscleGroup);
    }
  }, [route]);

  useEffect(() => {
    if (muscleGroup) {
      setActiveGroup(muscleGroup);
    }
  }, [muscleGroup]);

  const loadData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Initialize database
      const dbInitialized = await initDatabase();
      if (!dbInitialized) {
        throw new Error("Failed to initialize database");
      }

      // Load muscle groups
      let groups;
      if (Platform.OS === "web") {
        // Use static data for web
        let staticMuscleGroups;
        try {
          const db = require('../database/database');
          staticMuscleGroups = db.STATIC_MUSCLE_GROUPS || [];
        } catch (err) {
          setFatalError('Failed to load static data: ' + (err && err.message ? err.message : err));
          console.error('Static data import error:', err);
          staticMuscleGroups = [];
        }
        groups = staticMuscleGroups;
      } else {
        groups = await getMuscleGroups();
      }
      setMuscleGroups(groups);

      // Load exercises
      let exercisesData;
      if (Platform.OS === "web") {
        let staticExercises;
        try {
          const db = require('../database/database');
          staticExercises = db.STATIC_EXERCISES || [];
        } catch (err) {
          setFatalError('Failed to load static data: ' + (err && err.message ? err.message : err));
          console.error('Static data import error:', err);
          staticExercises = [];
        }
        exercisesData = staticExercises;
      } else {
        exercisesData = await getExercises();
      }

      // If muscleGroupId is provided, filter exercises for that muscle group
      const muscleGroupId = route?.params?.muscleGroupId || null;
      if (muscleGroupId) {
        const filteredExercises = exercisesData.filter(
          (exercise) => exercise.muscle_group_id === muscleGroupId
        );
        setExercises(filteredExercises);
      } else {
        setExercises(exercisesData);
      }

      setLoading(false);
    } catch (err) {
      setError("Error loading data: " + err);
      setLoading(false);
    }
  };

  const loadExercises = async () => {
    try {
      setError(null);
      console.log(
        "Loading exercises for muscleGroupId:",
        muscleGroupId,
        "activeGroup:",
        activeGroup
      );
      const exerciseData = await getExercises(
        activeGroup === "All" ? null : activeGroup
      );
      setExercises(exerciseData);

      if (activeGroup === "All") {
        getExercises().then(setExercises);
      } else {
        getExercises(activeGroup).then(setExercises);
      }
    } catch (err) {}

    const handleSelectExercise = (exercise) => {
      setSelectedExercises((prev) => {
        if (prev.find((e) => e.id === exercise.id)) {
          return prev.filter((e) => e.id !== exercise.id);
        }
        return [...prev, exercise];
      });
    };

    const handleSaveExercises = () => {
      // Only add exercises that are not already present
      const toAdd = selectedExercises.filter(
        (ex) => !safePreviousExercises.some((prev) => prev.id === ex.id)
      );
      if (route.params && route.params.returnToPreset) {
        // Save to preset flow: go back to the previous screen and pass exercises via navigation.navigate
        navigation.navigate({
          name: "Profile",
          params: {
            selectedExercisesForPreset: [...selectedExercises],
            showPresetModal: true,
          },
          merge: true,
        });
      } else if (toAdd.length > 0) {
        navigation.navigate("ConfigureWorkout", { addExercises: toAdd });
      } else {
        navigation.goBack();
      }
    };

    // Defensive: exercises fallback
    const safeExercises = Array.isArray(exercises) ? exercises : [];

    const filteredExercises = () => {
      const query = searchQuery.toLowerCase();
      return safeExercises.filter((exercise) =>
        exercise.name.toLowerCase().includes(query)
      );
    };

    const renderMuscleGroupButton = (group) => (
      <TouchableOpacity
        key={group ? group.id : "all"}
        style={[
          styles.groupButton,
          activeGroup === (group ? group.id : "All") &&
            styles.activeGroupButton,
        ]}
        onPress={() => {
          console.log("Selected muscle group:", group ? group.name : "All");
          setActiveGroup(group ? group.id : "All");
        }}
      >
        <Text
          style={[
            styles.groupButtonText,
            activeGroup === (group ? group.id : "All") &&
              styles.activeGroupButtonText,
          ]}
        >
          {group ? group.name : "All"}
        </Text>
      </TouchableOpacity>
    );

    const renderExerciseItem = ({ item }) => (
      <TouchableOpacity
        style={[
          styles.exerciseItem,
          selectedExercises.find((e) => e.id === item.id) &&
            styles.selectedExerciseItem,
        ]}
        onPress={() => handleSelectExercise(item)}
      >
        <View style={styles.exerciseContent}>
          <Text
            style={[
              styles.exerciseText,
              selectedExercises.find((e) => e.id === item.id) &&
                styles.selectedExerciseText,
            ]}
          >
            {item.name}
          </Text>
          {item.description && (
            <Text style={styles.exerciseDescription}>{item.description}</Text>
          )}
        </View>
        {selectedExercises.find((e) => e.id === item.id) && (
          <Ionicons name="checkmark-circle" size={24} color="#6b46c1" />
        )}
      </TouchableOpacity>
    );

    if (loading) {
      return (
        <View style={styles.loadingContainer}>
          <ActivityIndicator size="large" color="#6b46c1" />
        </View>
      );
    }

    if (error) {
      return (
        <View style={styles.errorContainer}>
          <Text style={styles.errorText}>{error}</Text>
          <TouchableOpacity style={styles.retryButton} onPress={loadData}>
            <Text style={styles.retryButtonText}>Retry</Text>
          </TouchableOpacity>
        </View>
      );
    }

    if (fatalError) {
      return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: '#fff' }}>
          <Text style={{ color: 'red', fontSize: 18 }}>Error: {fatalError}</Text>
        </View>
      );
    }

    // Defensive: muscleGroups fallback
    const safeMuscleGroups = Array.isArray(muscleGroups) ? muscleGroups : [];

    return (
      <View style={styles.container}>
        <View style={styles.searchContainer}>
          <Ionicons
            name="search"
            size={20}
            color="#666"
            style={styles.searchIcon}
          />
          <TextInput
            style={styles.searchInput}
            placeholder="Search exercises..."
            placeholderTextColor="#666"
            value={searchQuery}
            onChangeText={setSearchQuery}
          />
        </View>

        <View style={styles.groupButtonContainer}>
          {renderMuscleGroupButton(null)}
          {safeMuscleGroups.map((group) => renderMuscleGroupButton(group))}
        </View>

        <FlatList
          data={filteredExercises()}
          renderItem={renderExerciseItem}
          keyExtractor={(item) => item.id.toString()}
          style={styles.list}
          contentContainerStyle={styles.listContent}
        />

        <TouchableOpacity
          style={[
            styles.saveButton,
            selectedExercises.length === 0 && styles.disabledButton,
          ]}
          onPress={handleSaveExercises}
          disabled={selectedExercises.length === 0}
        >
          <Text style={styles.saveButtonText}>
            Add {selectedExercises.length} Exercise
            {selectedExercises.length !== 1 ? "s" : ""}
          </Text>
        </TouchableOpacity>
      </View>
    );
  };

