import React, { useState } from 'react';
import {
  View,
  Text,
  TouchableOpacity,
  FlatList,
  TextInput,
  StyleSheet,
  ScrollView,
  Picker,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';

const setTypes = [
  { label: 'Warmup', value: 'warmup' },
  { label: 'Numbered', value: 'numbered' },
  { label: 'Failure', value: 'failure' },
  { label: 'Drop', value: 'drop' },
  { label: 'Remove', value: 'remove' },
];

const ConfigureWorkoutScreen = ({ route, navigation }) => {
  const { exercises } = route.params || { exercises: [] };
  const [workoutName, setWorkoutName] = useState('');
  const [exerciseConfigs, setExerciseConfigs] = useState(
    exercises.map(exercise => ({
      ...exercise,
      sets: [
        {
          setType: 'numbered',
          reps: '12',
          weight: '',
          notes: '',
        },
      ],
    }))
  );

  const handleUpdateSet = (exerciseIdx, setIdx, field, value) => {
    const newConfigs = [...exerciseConfigs];
    const newSets = [...newConfigs[exerciseIdx].sets];
    newSets[setIdx] = { ...newSets[setIdx], [field]: value };
    newConfigs[exerciseIdx].sets = newSets;
    setExerciseConfigs(newConfigs);
  };

  const handleAddSet = (exerciseIdx) => {
    const newConfigs = [...exerciseConfigs];
    newConfigs[exerciseIdx].sets = [
      ...newConfigs[exerciseIdx].sets,
      {
        setType: 'numbered',
        reps: '12',
        weight: '',
        notes: '',
      },
    ];
    setExerciseConfigs(newConfigs);
  };

  const handleUpdateConfig = (index, field, value) => {
    const newConfigs = [...exerciseConfigs];
    newConfigs[index] = {
      ...newConfigs[index],
      [field]: value
    };
    setExerciseConfigs(newConfigs);
  };

  const handleSaveWorkout = () => {
    // Here you would save the workout configuration
    // For now, we'll just navigate back to home
    navigation.navigate('HomeScreen', {
      workout: {
        name: workoutName || 'My Workout',
        exercises: exerciseConfigs
      }
    });
  };

  const renderSet = (exerciseIdx, set, setIdx) => (
    <View key={setIdx} style={styles.setCard}>
      <Text style={styles.setNumber}>Set {setIdx + 1}</Text>
      <View style={styles.configRow}>
        <View style={styles.configItem}>
          <Text style={styles.label}>Set Type</Text>
          <Picker
            selectedValue={set.setType}
            style={styles.picker}
            onValueChange={value => handleUpdateSet(exerciseIdx, setIdx, 'setType', value)}
            mode="dropdown"
          >
            {setTypes.map(type => (
              <Picker.Item key={type.value} label={type.label} value={type.value} />
            ))}
          </Picker>
        </View>
        <View style={styles.configItem}>
          <Text style={styles.label}>Reps</Text>
          <TextInput
            style={styles.input}
            value={set.reps}
            onChangeText={value => handleUpdateSet(exerciseIdx, setIdx, 'reps', value)}
            keyboardType="numeric"
            placeholder="12"
            placeholderTextColor="#666"
          />
        </View>
        <View style={styles.configItem}>
          <Text style={styles.label}>Weight</Text>
          <TextInput
            style={styles.input}
            value={set.weight}
            onChangeText={value => handleUpdateSet(exerciseIdx, setIdx, 'weight', value)}
            keyboardType="numeric"
            placeholder="0"
            placeholderTextColor="#666"
          />
        </View>
      </View>
      <View style={styles.notesContainer}>
        <Text style={styles.label}>Notes</Text>
        <TextInput
          style={styles.notesInput}
          value={set.notes}
          onChangeText={value => handleUpdateSet(exerciseIdx, setIdx, 'notes', value)}
          placeholder="Add notes here..."
          placeholderTextColor="#666"
          multiline
        />
      </View>
    </View>
  );

  const renderExerciseConfig = ({ item, index }) => (
    <View style={styles.exerciseCard}>
      <Text style={styles.exerciseName}>{item.name}</Text>
      <Text style={styles.exerciseDesc}>{item.description}</Text>
      {item.sets.map((set, setIdx) => renderSet(index, set, setIdx))}
      <TouchableOpacity style={styles.addSetButton} onPress={() => handleAddSet(index)}>
        <Text style={styles.addSetButtonText}>+ Add Set</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <TextInput
          style={styles.workoutNameInput}
          value={workoutName}
          onChangeText={setWorkoutName}
          placeholder="Workout Name"
          placeholderTextColor="#666"
        />
      </View>

      <FlatList
        data={exerciseConfigs}
        renderItem={renderExerciseConfig}
        keyExtractor={(item, index) => index.toString()}
        contentContainerStyle={styles.exerciseList}
      />

      <TouchableOpacity
        style={styles.saveButton}
        onPress={handleSaveWorkout}
      >
        <Text style={styles.saveButtonText}>Save Workout</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#171923',
  },
  header: {
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#2D3748',
  },
  workoutNameInput: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#fff',
    padding: 8,
    backgroundColor: '#2D3748',
    borderRadius: 8,
  },
  exerciseList: {
    padding: 16,
  },
  exerciseCard: {
    backgroundColor: '#2D3748',
    borderRadius: 12,
    padding: 16,
    marginBottom: 16,
  },
  exerciseName: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#fff',
    marginBottom: 16,
  },
  exerciseDesc: {
    color: '#a0aec0',
    fontSize: 13,
    marginBottom: 4,
  },
  configRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 16,
  },
  configItem: {
    flex: 1,
    marginHorizontal: 4,
  },
  label: {
    color: '#fff',
    marginBottom: 4,
    fontSize: 14,
  },
  input: {
    backgroundColor: '#1A202C',
    borderRadius: 8,
    padding: 8,
    color: '#fff',
    textAlign: 'center',
  },
  picker: {
    minWidth: 120,
    backgroundColor: '#22223b',
    color: '#fff',
    borderRadius: 8,
    marginVertical: 4,
  },
  notesContainer: {
    marginTop: 8,
  },
  notesInput: {
    backgroundColor: '#1A202C',
    borderRadius: 8,
    padding: 8,
    color: '#fff',
    height: 80,
    textAlignVertical: 'top',
  },
  setCard: {
    marginVertical: 6,
    padding: 8,
    backgroundColor: '#23263a',
    borderRadius: 10,
  },
  setNumber: {
    color: '#b794f4',
    fontWeight: 'bold',
    marginBottom: 2,
    fontSize: 14,
  },
  addSetButton: {
    backgroundColor: '#6b46c1',
    paddingVertical: 6,
    paddingHorizontal: 18,
    borderRadius: 16,
    alignSelf: 'flex-start',
    marginTop: 8,
  },
  addSetButtonText: {
    color: '#fff',
    fontWeight: 'bold',
    fontSize: 14,
  },
  saveButton: {
    backgroundColor: '#6b46c1',
    margin: 16,
    padding: 16,
    borderRadius: 8,
    alignItems: 'center',
  },
  saveButtonText: {
    color: '#fff',
    fontSize: 16,
    fontWeight: 'bold',
  },
});

export default ConfigureWorkoutScreen;
