import React from 'react';
import { View, Text, ScrollView, StyleSheet } from 'react-native';

const AboutScreen = () => {
  return (
    <ScrollView style={styles.container}>
      <View style={styles.content}>
        <Text style={styles.title}>About TFC</Text>
        <Text style={styles.subtitle}>Training Frequency Calculator</Text>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>What is TFC?</Text>
          <Text style={styles.text}>
            TFC (Training Frequency Calculator) is a specialized tool designed to help athletes and fitness enthusiasts optimize their training frequency by tracking muscle recovery times.
          </Text>
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Key Features</Text>
          <Text style={styles.listItem}>• Recovery tracking for different muscle groups</Text>
          <Text style={styles.listItem}>• Personalized training recommendations</Text>
          <Text style={styles.listItem}>• Progress monitoring and analytics</Text>
          <Text style={styles.listItem}>• Easy-to-use interface</Text>
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>How It Works</Text>
          <Text style={styles.text}>
            TFC uses scientifically-backed recovery times for different muscle groups and tracks your workouts to provide personalized recommendations for optimal training frequency.
          </Text>
        </View>

        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Contact</Text>
          <Text style={styles.text}>
            For support or feedback, please contact us at support@tfc-app.com
          </Text>
        </View>

        <Text style={styles.version}>Version 1.0.0</Text>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  content: {
    padding: 20,
  },
  title: {
    fontSize: 28,
    fontWeight: 'bold',
    color: '#2d3748',
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 18,
    color: '#6b46c1',
    marginBottom: 24,
  },
  section: {
    marginBottom: 24,
  },
  sectionTitle: {
    fontSize: 20,
    fontWeight: '600',
    color: '#2d3748',
    marginBottom: 12,
  },
  text: {
    fontSize: 16,
    color: '#4a5568',
    lineHeight: 24,
    marginBottom: 12,
  },
  listItem: {
    fontSize: 16,
    color: '#4a5568',
    lineHeight: 24,
    marginBottom: 8,
    paddingLeft: 8,
  },
  version: {
    fontSize: 14,
    color: '#a0aec0',
    textAlign: 'center',
    marginTop: 32,
  },
});

export default AboutScreen;
