import React, { useState } from 'react';
import { View, Text, TextInput, StyleSheet, Alert } from 'react-native';
import { useDispatch, useSelector } from 'react-redux';
import { forgotPassword, recoverUsername } from '../services/authService';
import { setRecoveryStatus, setRecoveryError, clearRecoveryState } from '../redux/authSlice';
import Button from '../styles/Button';

const RecoveryScreen = ({ navigation }) => {
  const [email, setEmail] = useState('');
  const [mode, setMode] = useState('password'); // 'password' or 'username'
  const dispatch = useDispatch();
  const { recoveryStatus, recoveryError } = useSelector((state) => state.auth);

  const handleRecovery = async () => {
    if (!email) {
      Alert.alert('Error', 'Please enter your email address');
      return;
    }

    try {
      dispatch(clearRecoveryState());

      if (mode === 'password') {
        await forgotPassword(email);
        dispatch(setRecoveryStatus('Password reset instructions sent to your email'));
      } else {
        await recoverUsername(email);
        dispatch(setRecoveryStatus('Username sent to your email'));
      }
    } catch (error) {
      dispatch(setRecoveryError(error.toString()));
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Account Recovery</Text>

      <View style={styles.toggleContainer}>
        <Button
          title="Reset Password"
          onPress={() => setMode('password')}
          style={[styles.toggleButton, mode === 'password' && styles.activeToggle]}
        />
        <Button
          title="Recover Username"
          onPress={() => setMode('username')}
          style={[styles.toggleButton, mode === 'username' && styles.activeToggle]}
        />
      </View>

      <TextInput
        style={styles.input}
        placeholder="Enter your email"
        value={email}
        onChangeText={setEmail}
        keyboardType="email-address"
        autoCapitalize="none"
      />

      {recoveryStatus && <Text style={styles.successText}>{recoveryStatus}</Text>}
      {recoveryError && <Text style={styles.errorText}>{recoveryError}</Text>}

      <Button
        title={mode === 'password' ? 'Reset Password' : 'Recover Username'}
        onPress={handleRecovery}
      />

      <Button
        title="Back to Login"
        onPress={() => navigation.goBack()}
        style={styles.backButton}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#fff',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
    textAlign: 'center',
  },
  toggleContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 20,
  },
  toggleButton: {
    flex: 1,
    marginHorizontal: 5,
  },
  activeToggle: {
    backgroundColor: '#4a90e2',
  },
  input: {
    borderWidth: 1,
    borderColor: '#ddd',
    padding: 10,
    borderRadius: 5,
    marginBottom: 20,
  },
  successText: {
    color: 'green',
    marginBottom: 10,
  },
  errorText: {
    color: 'red',
    marginBottom: 10,
  },
  backButton: {
    marginTop: 20,
    backgroundColor: '#6c757d',
  },
});

export default RecoveryScreen;
