  76: 'Back', 77: 'Back', 78: 'Back',

  // Shoulders (5) - Mapped to 'Shoulders' in Redux store
  79: 'Shoulders', 80: 'Shoulders', 81: 'Shoulders', 82: 'Shoulders', 83: 'Shoulders',
