import React from "react";
import { View, Text, ScrollView, StyleSheet } from "react-native";
import { useSelector } from "react-redux";

const MuscleRecoveryMeter = ({ value }) => {
  // Value should be between 0-100
  let color;
  if (value < 33) {
    color = '#FF4444'; // Red
  } else if (value < 66) {
    color = '#FFBB33'; // Yellow
  } else {
    color = '#00C851'; // Green
  }

  return (
    <View style={styles.meterContainer}>
      <View style={[styles.meter, { backgroundColor: '#e0e0e0' }]}>
        <View 
          style={[
            styles.meterFill, 
            { 
              width: `${value}%`,
              backgroundColor: color 
            }
          ]} 
        />
      </View>
      <View style={styles.meterLabels}>
        <Text style={[styles.meterLabel, { color: '#FF4444' }]}>Rest</Text>
        <Text style={[styles.meterLabel, { color: '#FFBB33' }]}>Caution</Text>
        <Text style={[styles.meterLabel, { color: '#00C851' }]}>Train</Text>
      </View>
    </View>
  );
};

const RecoveryGuideScreen = () => {
  const muscleGroups = useSelector(state => {
    const groups = state.recovery.muscleGroups;
    return Object.entries(groups).map(([name, data]) => ({
      name: name.charAt(0).toUpperCase() + name.slice(1),
      recovery: data.recovery
    }));
  });

  if (!muscleGroups) {
    return (
      <View style={styles.container}>
        <Text>Loading...</Text>
      </View>
    );
  }

  const sortedMuscleGroups = [...muscleGroups].sort((a, b) => a.name.localeCompare(b.name));

  return (
    <ScrollView style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Recovery Guide</Text>
        <Text style={styles.subtitle}>
          Monitor your muscle recovery status to optimize your training
        </Text>
      </View>

      <View style={styles.muscleList}>
        {sortedMuscleGroups.map((muscle, index) => (
          <View key={index} style={styles.muscleItem}>
            <Text style={styles.muscleName}>{muscle.name}</Text>
            <View style={styles.meterWrapper}>
              <MuscleRecoveryMeter value={muscle.recovery} />
            </View>
          </View>
        ))}
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#F5FCFF',
  },
  header: {
    padding: 20,
    backgroundColor: '#fff',
    borderBottomWidth: 1,
    borderBottomColor: '#e0e0e0',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 8,
  },
  subtitle: {
    fontSize: 16,
    color: '#666',
    lineHeight: 22,
  },
  muscleList: {
    padding: 15,
  },
  muscleItem: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingVertical: 12,
    borderBottomWidth: 1,
    borderBottomColor: '#e0e0e0',
  },
  muscleName: {
    flex: 1,
    fontSize: 18,
    color: '#333',
    fontWeight: '500',
  },
  meterWrapper: {
    flex: 2,
  },
  meterContainer: {
    width: '100%',
  },
  meter: {
    height: 12,
    borderRadius: 6,
    overflow: 'hidden',
  },
  meterFill: {
    height: '100%',
    borderRadius: 6,
  },
  meterLabels: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 4,
  },
  meterLabel: {
    fontSize: 12,
    fontWeight: '500',
  },
});

export default RecoveryGuideScreen;
